import mongoose from 'mongoose/browser';

mongoose.connect('mongodb://localhost/herosdb', {
    useUnifiedTopology: true,
    useNewUrlParser: true
})
    .then(db => console.log('DB is connected'))
    .catch(err => console.error(err));
